INSERT INTO `dr_server_conferenceinfo` VALUES ('1193', 'International Conference on Electronics, Computers and Artificial Intelligence', 'ECAI\'', ' ', 'Bucharest, Romania', '2020-06-25', '2020-05-04', '2020-06-01', '12', 'The conference is aimed to serve as an international forum for effective exchange of scientific knowledge and experience among researchers active in various theoretical and applied areas of electronic equipments, communication, automatic control, applied informatics, information technology and computer science.\r\n\r\nThe program will include plenary and regular sessions, special sessions, workshops, discussions’ groups and social events.\r\n\r\nThe conference’s official language is English and areas of interest are:\r\n\r\n    Electronic circuits and equipments;\r\n    Communications;\r\n    Software, databases, and computer applications;\r\n    Artificial Intelligence, including Expert systems;\r\n    Signal and image processing;\r\n    Microwaves - techniques and technologies & EMC;\r\n    Bio-medical applications & biomaterials;\r\n    Control, robotics and mechatronics;\r\n    Electrical engineering applications;\r\n    Energy & Environmental issues;\r\n    Educational multimedia applications;\r\n    Other topics are welcome', '2020-05-05 12:06:27.778840', '0', '0', ' ', ' ', ' ', ' ', '', '', ' ');
